export default {
   login: (token) => {
        localStorage.setItem("TOKEN", token);
    },

    logout: () => {
        localStorage.removeItem("TOKEN");
    },

    getToken: () => {
        localStorage.getItem("TOKEN");
    },

    isAuth: () =>{
        localStorage.getItem('TOKEN') !== null;
    }
}
